#define AOFFSET 48
using namespace std;

int ctoi( int c )
{
    return c - '0';
}

int main(int argc, char const *argv[])
{

    char num[] = "";

    unsigned long long int survivor = 0;
    unsigned long long int tmp = 0;

    for (size_t i = 0; i < 999; i+=13) {
        for (size_t x = 1; x <= 13; x++) {
            if (i + x == 999){
                break;
            }
            int y = num[i+x] - AOFFSET;
            cout << y << endl;
            tmp = tmp * y;
        }
        if (tmp > survivor) {
            survivor = tmp;
        }
        tmp = 1;
    }

    //int i = num[0] - 48;

    cout << survivor << endl;

    return 0;
}
